// src/components/Gallery.js
import React, { useEffect, useState } from 'react';
import { firestore } from '../firebaseConfig';

const Gallery = () => {
  const [drawings, setDrawings] = useState([]);

  useEffect(() => {
    const unsubscribe = firestore.collection('drawings')
      .orderBy('createdAt', 'desc')
      .onSnapshot(snapshot => {
        const fetchedDrawings = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setDrawings(fetchedDrawings);
      });

    return () => unsubscribe(); // Cleanup subscription on unmount
  }, []);

  return (
    <div>
      <h2>Gallery</h2>
      {drawings.length === 0 ? (
        <p>No drawings available</p>
      ) : (
        drawings.map(drawing => (
          <div key={drawing.id} style={{ marginBottom: '20px' }}>
            <img src={drawing.url} alt="User drawing" style={{ maxWidth: '100%', height: 'auto' }} />
            <p>Drawn by: {drawing.displayName}</p> {/* Show display name */}
          </div>
        ))
      )}
    </div>
  );
};

export default Gallery;
