// src/components/Gallery.js
import React, { useEffect, useState } from 'react';
import { firestore } from '../firebaseConfig';

const Gallery = () => {
  const [drawings, setDrawings] = useState([]);

  useEffect(() => {
    const unsubscribe = firestore.collection('drawings').orderBy('createdAt', 'desc')
      .onSnapshot(snapshot => {
        const fetchedDrawings = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setDrawings(fetchedDrawings);
      });
    return () => unsubscribe();
  }, []);

  return (
    <div>
      {drawings.map(drawing => (
        <div key={drawing.id}>
          <img src={drawing.url} alt="user drawing" />
        </div>
      ))}
    </div>
  );
};

export default Gallery;
