// src/components/DrawCanvas.js
import React, { useRef } from 'react';
import CanvasDraw from 'react-canvas-draw';
import { storage, firestore, auth } from '../firebaseConfig';
import { ref, uploadString, getDownloadURL } from 'firebase/storage';
import { collection, addDoc } from 'firebase/firestore'; // Importing Firestore functions

const DrawCanvas = () => {
  const canvasRef = useRef();

  const saveDrawing = async () => {
    const user = auth.currentUser;

    if (user) {
      // Get the drawing as a data URL (PNG image)
      const drawingData = canvasRef.current.getDataURL();

      // Create a reference to the storage location
      const storageRef = ref(storage, `drawings/${user.uid}/${Date.now()}.png`);

      // Upload the drawing data
      await uploadString(storageRef, drawingData, 'data_url');

      // Get the download URL
      const url = await getDownloadURL(storageRef);

      // Save the drawing info to Firestore
      await addDoc(collection(firestore, 'drawings'), {
        url,
        userId: user.uid,
        createdAt: new Date(),
      });
    }
  };

  return (
    <div>
      <CanvasDraw ref={canvasRef} />
      <button onClick={saveDrawing}>Save Drawing</button>
    </div>
  );
};

export default DrawCanvas;
